import { VirtualClass } from "./common";
/**
 * Single mixin.
 *
 * @export
 * @template T
 * @param {{ new () : T; }} component
 * @returns {VirtualClass<T>}
 */
export declare function Mixin<T>(component: {
    new (): T;
}): VirtualClass<T>;
/**
 * Multiple mixins.
 *
 * @export
 * @template T
 * @param {...{ new () : any; }[]} components
 * @returns {VirtualClass<T>}
 */
export declare function Mixins<T>(...components: {
    new (): any;
}[]): VirtualClass<T>;
